INSERT INTO `QUOTES` (AUTHOR,QUOTE) VALUES ('Eric Hoffer','Man is the only creature that strives to surpass himself, and yearns for the impossible.');
INSERT INTO `QUOTES` (AUTHOR,QUOTE) VALUES ('Eric Hoffer','One of the marks of a truly vigorous society is the ability to dispense with passion as a midwife of action - the ability to pass directly from thought to action.');
INSERT INTO `QUOTES` (AUTHOR,QUOTE) VALUES ('Eric Hoffer','Passionate hatred can give meaning and purpose to an empty life.');
INSERT INTO `QUOTES` (AUTHOR,QUOTE) VALUES ('Eric Hoffer','The best part of the art of living is to know how to grow old gracefully.');
